<?php

return [
    'int' => fn($data) => is_numeric($data),
    'str' => fn($data) => is_string($data),
    'arr' => fn($data) => is_array($data),
    'float' => fn($data) => is_float($data),
    'in' => fn($data, $params) => in_array($data, $params),
    'min' => fn($data, $param) => $data >= $param,
    'strMin' => fn($data, $param) => strlen($data) >= $param,
    'arrMin' => fn($data, $param) => count($data) >= $param,
    'max' => fn($data, $param) => $data <= $param,
    'strMax' => fn($data, $param) => strlen($data) <= $param,
    'arrMax' => fn($data, $param) => count($data) <= $param,
    'btw' => fn($data, $params) => $params[0] <= $data && $data <= $params[1],
    'strBtw' => fn($data, $params) => $params[0] <= strlen($data) && strlen($data) <= $params[1],
    'arrBtw' => fn($data, $params) => $params[0] <= count($data) && count($data) <= $params[1],
    'strLen' => fn($data, $param) => strlen($data) === $param,
    'arrLen' => fn($data, $param) => count($data) === $param,
    'safe' => fn($data) => (bool)preg_match('@^[\S]+$@', $data),
    'ip4' => fn($data) => (bool)preg_match('@^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$@', $data),
    'ip6' => fn($data) => (bool)preg_match('@^((([0-9A-Fa-f]{1,4}:){7}[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){6}:[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){5}:([0-9A-Fa-f]{1,4}:)?[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){4}:([0-9A-Fa-f]{1,4}:){0,2}[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){3}:([0-9A-Fa-f]{1,4}:){0,3}[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){2}:([0-9A-Fa-f]{1,4}:){0,4}[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){6}((\b((25[0-5])|(1\d{2})|(2[0-4]\d)|(\d{1,2}))\b)\.){3}(\b((25[0-5])|(1\d{2})|(2[0-4]\d)|(\d{1,2}))\b))|(([0-9A-Fa-f]{1,4}:){0,5}:((\b((25[0-5])|(1\d{2})|(2[0-4]\d)|(\d{1,2}))\b)\.){3}(\b((25[0-5])|(1\d{2})|(2[0-4]\d)|(\d{1,2}))\b))|(::([0-9A-Fa-f]{1,4}:){0,5}((\b((25[0-5])|(1\d{2})|(2[0-4]\d)|(\d{1,2}))\b)\.){3}(\b((25[0-5])|(1\d{2})|(2[0-4]\d)|(\d{1,2}))\b))|([0-9A-Fa-f]{1,4}::([0-9A-Fa-f]{1,4}:){0,5}[0-9A-Fa-f]{1,4})|(::([0-9A-Fa-f]{1,4}:){0,6}[0-9A-Fa-f]{1,4})|(([0-9A-Fa-f]{1,4}:){1,7}:))$@i', $data),
    'idCard' => fn($data) => (bool)preg_match('@^[1-9]\d{5}(?:18|19|20)\d{2}(?:0[1-9]|10|11|12)(?:0[1-9]|[1-2]\d|30|31)\d{3}[\dXx]$@', $data),
    'uuid' => fn($data) => (bool)preg_match('@@', $data),
    'phone' => fn($data) => (bool)preg_match('@^(086|86)?1[3-9]{2}\d{8}$@', $data),
    'email' => fn($data) => (bool)preg_match('@^[A-Za-z0-9\u4e00-\u9fa5]+@[a-zA-Z0-9_-]+(\.[a-zA-Z0-9_-]+)+$@', $data),
    'qq' => fn($data) => (bool)preg_match('@^[1-9][0-9]{4,10}$@', $data),
    'date' => fn($data) => (bool)preg_match('@^((([0-9]{3}[1-9]|[0-9]{2}[1-9][0-9]|[0-9][1-9][0-9]{2}|[1-9][0-9]{3})-(((0[13578]|1[02])-(0[1-9]|[12][0-9]|3[01]))|((0[469]|11)-(0[1-9]|[12][0-9]|30))|(02-(0[1-9]|[1][0-9]|2[0-8]))))|((([0-9]{2})(0[48]|[2468][048]|[13579][26])|((0[48]|[2468][048]|[3579][26])00))-02-29))\\s+([0-1]?[0-9]|2[0-3]):([0-5][0-9]):([0-5][0-9])$@', $data),
];